matlab -nodisplay -nosplash -r "projectDir = '/data/jag/MELA/MelanopsinMR';subjDirs = listdir(projectDir,'dirs');for i = 4;if ~strcmp(subjDirs{i},'logs');sessDirs = listdir(fullfile(projectDir,subjDirs{i}),'dirs');for j = 1;boldDirs = find_bold(fullfile(projectDir,subjDirs{i},sessDirs{j}));for k = 1:length(boldDirs);temporal_filter(fullfile(projectDir,subjDirs{i},sessDirs{j}),k,'wdrf','high',0.01,0.1);smooth_vol_surf(fullfile(projectDir,subjDirs{i},sessDirs{j}),k,5,'wdrf.tf');end;end;end;end"